jQuery(window).on("load", function () {
  $("#preloader").fadeOut(500);
  $("#main-wrapper").addClass("show");

  $("body").attr("data-sidebar-style") === "mini"
    ? $(".hamburger").addClass("is-active")
    : $(".hamburger").removeClass("is-active");
});

(function ($) {
  "use strict";

  $("#menu").metisMenu();

  $(".nk-nav-scroll").slimscroll({
    position: "right",
    size: "5px",
    height: "100%",
    color: "transparent",
  });

  $(".nav-control").on("click", function () {
    $("#main-wrapper").toggleClass("menu-toggle");

    $(".hamburger").toggleClass("is-active");
  });

  $(function () {
    for (
      var nk = window.location,
        o = $("ul#menu a")
          .filter(function () {
            return this.href == nk;
          })
          .addClass("active")
          .parent()
          .addClass("active");
      ;

    ) {
      if (!o.is("li")) break;
      o = o.parent().addClass("in").parent().addClass("active");
    }
  });

  var resizeTimeout;

  function adjustContentMinHeight() {
    var $content = $(".content-body").first();
    if (!$content.length) {
      return;
    }

    var viewportHeight = Math.max(
      window.innerHeight || 0,
      document.documentElement ? document.documentElement.clientHeight || 0 : 0
    );

    if (!viewportHeight) {
      return;
    }

    var $footer = $(".footer").first();
    if (!$footer.length) {
      return;
    }

    var footerHeight = $footer.outerHeight(true) || 0;
    var contentOffsetTop = ($content.offset() && $content.offset().top) || 0;

    // Calculate the minimum height needed to push footer to bottom
    var minHeight = viewportHeight - contentOffsetTop - footerHeight;
    
    if (minHeight < 0) {
      minHeight = 0;
    }

    $content.css("min-height", minHeight + "px");
    
    // Multi-pass adjustment to handle layout reflow
    if (window.requestAnimationFrame) {
      var maxPasses = 3;
      var passCount = 0;
      
      function checkAndAdjust() {
        passCount++;
        
        window.requestAnimationFrame(function() {
          // Get the actual footer position using getBoundingClientRect for accuracy
          var footerRect = $footer[0].getBoundingClientRect();
          var footerBottom = footerRect.bottom;
          
          // Calculate how much we're off from the viewport bottom
          var diff = viewportHeight - footerBottom;
          
          // If footer is not at the bottom (diff > 1px) and we haven't exceeded max passes
          if (Math.abs(diff) > 1 && passCount < maxPasses) {
            var currentMinHeight = parseFloat($content.css("min-height")) || 0;
            var newMinHeight = currentMinHeight + diff;
            
            if (newMinHeight < 0) {
              newMinHeight = 0;
            }
            
            $content.css("min-height", newMinHeight + "px");
            
            // Check again after this adjustment
            checkAndAdjust();
          }
        });
      }
      
      checkAndAdjust();
    }
  }

  $(function () {
    adjustContentMinHeight();
  });

  $(window).on("load.contentBodyAdjust", adjustContentMinHeight);

  $(window).on("resize.contentBodyAdjust", function () {
    clearTimeout(resizeTimeout);
    resizeTimeout = setTimeout(adjustContentMinHeight, 100);
  });

  $(".selectpicker").selectpicker();

  $('[data-toggle="popover"]').popover();
  $('[data-toggle="tooltip"]').tooltip();

  const headerHight = $(".header").innerHeight();

  $(window).scroll(function () {
    if (
      $("body").attr("data-layout") === "horizontal" &&
      $("body").attr("data-header-position") === "static" &&
      $("body").attr("data-sidebar-position") === "fixed"
    )
      $(this.window).scrollTop() >= headerHight
        ? $(".metismenu").addClass("fixed")
        : $(".metismenu").removeClass("fixed");
  });

  $(".sidebar-right-trigger").on("click", function () {
    $(".sidebar-right").toggleClass("show");
  });
})(jQuery);
